INSERT INTO `classes` (`Name`, `HitDice`, `HitPointsAtFirst`, `HitPointsAtHigher`, `ArmorProf`, `WeaponProf`, `ToolProf`, `SavingThrowProf`, `SkillsProf`) VALUES
('Barbarian', '1d12 per barbarian level', '12+ your Constitution modifier', '1d12(or 7) + your Constitution modifier per barbarian level after 1st', 'Light armor, medium armor, sheilds', 'Simple weapons, martial weapons', 'None', 'Strength, Constitution', 'Choose two from Animal Handling, Athletics, Intimidation, Nature, Perception, and Survival'),
('Bard', '1d8 per bard level', '8 + your Constitution modifier', '1d8 (or 5) + your Constitution modifier per bard level after 1st', 'Light armor', 'Simple weapons, hand crossbows, longswords, rapiers, shortswords', 'Three musical instruments of your choice', 'Dexterity, Charisma', 'Choose any three');

